export function getCompetitorData(market) {
  const marketKey = market.toLowerCase().replace(/\s+/g, '_');
  return competitorData[marketKey] || {
    shareOfVoice: [],
    competitorStrengths: {},
    competitorWeaknesses: {},
    gapsToExploit: []
  };
}

